    private void btnInsertarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnInsertarActionPerformed
        
        if (txtNombre.getText().isEmpty() || txtDeporte.getText().isEmpty() || txtEdad.getText().isEmpty() || txtPais.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, evt, "No puedes dejar los campos vac√≠os.", WIDTH);
        } else {
            
            gLiga.insertarJugador(txtNombre.getText(), txtDeporte.getText(), txtCiudad.getText(), Integer.parseInt(txtEdad.getText()), txtPais.getText().toLowerCase());
            
            JOptionPane.showMessageDialog(this, "Jugador insertado.");
            
            vaciarCajas();
        }
        
    }//GEN-LAST:event_btnInsertarActionPerformed

    public void vaciarCajas(){
        txtNombre.setText("");
        txtDeporte.setText("");
        txtCiudad.setText("");
        txtEdad.setText("");
        txtPais.setText("");
    }
    
